INSERT INTO people (ext_id, first_name, last_name, honorific, birthyear, deathyear, pref_label, orig_label, bnf_notes, created_at, updated_at) VALUES (159, 'Michel', 'Guyot de Merville', NULL, 1676, 1755, 'Guyot de Merville (Michel)', NULL, 'Homme de lettres. - Voyagea en Europe, se fixa un temps  à La Haye, où il publia le périodique "Histoire littéraire de l''Europe". - Polémiqua avec Voltaire. - Fit jouer du théâtre à la Comédie française et au Théâtre italien', current_timestamp, current_timestamp);
