INSERT INTO people (ext_id, first_name, last_name, honorific, birthyear, deathyear, pref_label, orig_label, bnf_notes, created_at, updated_at) VALUES (169, 'Louis', 'Dussieux', NULL, 1744, 1805, 'Dussieux (Louis)', NULL, 'Écrivain, historien et agronome. - Journaliste, rédacteur du "Journal de Paris", collaborateur de la "Collection universelle des Mémoires particuliers relatifs à l''Histoire de France". - Proscrit comme Girondin, membre du conseil des Anciens A aussi traduit de l''italien et de l''allemand en français', current_timestamp, current_timestamp);
INSERT INTO people (ext_id, first_name, last_name, honorific, birthyear, deathyear, pref_label, orig_label, bnf_notes, created_at, updated_at) VALUES (116, 'Barthélemy', 'Imbert', NULL, 1747, 1790, 'Imbert (Barthélemy)', NULL, 'Variante(s) de prénom : Bartolommeo. - Homme de lettres, journaliste et éditeur. - Fils d''un fabricant de bas de Nîmes. Membre de l''académie de Nîmes. Se fixe à Paris en 1770 ou peu avant. Auteur dramatique, poète et romancier. Emprisonné en janv. 1775 au For(t)-l''Évêque pour des couplets insultants à la mémoire du feu roi Louis XV. En faillite en fév. 1777, il se retire pour un temps à Liège avant de rentrer à Paris. Rédacteur de 1778 à 1790 de la partie dramatique du "Mercure de France" dirigé par Charles-Joseph Panckoucke, dont il est directeur adjoint à partir de 1786 env. Collabore aussi à la "Bibliothèque universelle des romans" à partir de 1779 env. Coéditeur avec Claude-Sixte Sautreau de Marsy des "Annales poétiques..." qu''ils diffusent de 1779 à 1788 à leur adresse ("chez les éditeurs"), successivement rue Saint-Nicaise et rue de la Jussienne. Décédé à Paris en août 1790', current_timestamp, current_timestamp);
