INSERT INTO people (ext_id, first_name, last_name, honorific, birthyear, deathyear, pref_label, orig_label, bnf_notes, created_at, updated_at) VALUES (291, 'François', 'Belin', NULL, 1748, 1808, 'Belin (François)', NULL, 'Autre(s) graphie(s) : Bélin. - Imprimeur-libraire ; imprimeur du premier tribunal criminel du département de Paris (1792). - Natif de Genevrières, près de Langres, fils d''un recteur d''école et receveur des traites foraines. Monté à Paris avant le 2 oct. 1770, date à laquelle il obtient un certificat de congruité de l''université. En apprentissage à partir de déc. 1773. Reçu libraire le 10 mars 1777, il acquiert le fonds de Denis Humblot, puis monte une imprimerie à partir de l''été 1792, en association avec son voisin Claude II Simon. Officier de la garde nationale (lieutenant des fusiliers de Saint-Étienne-du-Mont) en 1789-1790. Membre de la 1re assemblée électorale de Paris, réunie à partir d''oct. 1790. Emprisonné du 12 avril au 8 août 1794 pour avoir mis en tête d''une éd. de la Constitution des idées préliminaires jugées subversives. Membre du comité civil de la section Marat en l''an II. À nouveau électeur de Paris en 1796 ; notable en 1801. Cède son imprimerie à Jean Gratiot en 1804. Se retire à Sceaux où il a acquis une maison de campagne en 1801. Décédé à Paris le 10 déc. 1808 ; inventaire après décès 19 déc. 1808. Vente de sa bibliothèque en 1810 (catalogue impr.). Sa veuve lui succède', current_timestamp, current_timestamp);
INSERT INTO people (ext_id, first_name, last_name, honorific, birthyear, deathyear, pref_label, orig_label, bnf_notes, created_at, updated_at) VALUES (64, 'Prosper Jolyot de', 'Crébillon', NULL, 1674, 1762, 'Crébillon (Prosper Jolyot de)', 'Crébillon père', 'Auteur dramatique. - Membre de l''Académie française (élu en 1731). - Acquis la terre de Crébillon ou Crais-Billon (en 1686). - Père du romancier : Crébillon, Claude-Prosper Jolyot de (1707-1777)', current_timestamp, current_timestamp);
